import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import { Container, TextField, Button, Typography, Box, Paper, CircularProgress, Grid } from '@mui/material';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Login = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [emailLogin, setEmailLogin] = useState('');
  const [senhaLogin, setSenhaLogin] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    const response = await fetch('http://localhost:5000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: emailLogin, senha: senhaLogin })
    });
    const data = await response.json();
    setIsLoading(false);
    if (response.ok) {
      toast.success('Login realizado com sucesso!');
      localStorage.setItem('token', data.access_token);
      navigate('/dashboard'); // Redireciona para o dashboard após login bem-sucedido
    } else {
      toast.error(data.mensagem || 'Erro ao fazer login');
    }
  };

  return (
    <Container maxWidth="sm">
      <ToastContainer />
      <Paper elevation={3} sx={{ padding: 4, marginTop: 8 }}>
        <Typography variant="h4" gutterBottom>
          {t('Login')}
        </Typography>
        <Box component="form" onSubmit={handleLogin} noValidate>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                label={t('Email')}
                type="email"
                fullWidth
                margin="normal"
                variant="outlined"
                value={emailLogin}
                onChange={(e) => setEmailLogin(e.target.value)}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label={t('Senha')}
                type="password"
                fullWidth
                margin="normal"
                variant="outlined"
                value={senhaLogin}
                onChange={(e) => setSenhaLogin(e.target.value)}
                required
              />
            </Grid>
          </Grid>
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ marginTop: 2 }}>
            {isLoading ? <CircularProgress size={24} /> : t('Login')}
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default Login;
